$paragraphList  = explode("\n", $textVeryLong);
foreach ($paragraphList as $index => $paragraph) {
    $phrasesList = explode(".", $paragraph);

    foreach ($phrasesList as $indexPhrase => $phrase) {
        $phrasesList[$indexPhrase] = trim($phrase);
    }

    if (strlen($phrasesList[count($phrasesList) - 1]) === 0) {
        array_pop($phrasesList);
    }

    $paragraphList[$index] = $phrasesList;
}

